import React from 'react';

function InputEx({ fromCurrency, toCurrency, amount, onAmountChange, onSwitch, onFromCurrencyChange, onToCurrencyChange }) {
    return (
        <div>
            <select value={fromCurrency} onChange={(e) => onFromCurrencyChange(e.target.value)}>
                <option value="USD">USD</option>
                <option value="ILS">ILS</option>
                <option value="EUR">EUR</option>
                {/* אפשר להוסיף עוד מטבעות כאן */}
            </select>
            <input type="number" value={amount} onChange={(e) => onAmountChange(Number(e.target.value))} />
            <button onClick={onSwitch}>↔</button>
            <select value={toCurrency} onChange={(e) => onToCurrencyChange(e.target.value)}>
                <option value="USD">USD</option>
                <option value="ILS">ILS</option>
                <option value="EUR">EUR</option>
                {/* אפשר להוסיף עוד מטבעות כאן */}
            </select>
        </div>
    );
}

export default InputEx;
