import React, { useState, useEffect } from 'react';
import axios from 'axios';
import InputEx from './InputEx';
import ScoreEx from './ScoreEx';

function ExchangeApp() {
    const [currencies, setCurrencies] = useState([]);
    const [baseAmount, setBaseAmount] = useState(100);
    const [baseCurrency, setBaseCurrency] = useState('USD');
    const [targetCurrency, setTargetCurrency] = useState('ILS');
    const [conversionRate, setConversionRate] = useState({});
    const [result, setResult] = useState(0);
    const [date, setDate] = useState(new Date());

    useEffect(() => {
        axios.get('https://monkeys.co.il/api2/currency.php').then(response => {
            setCurrencies(Object.keys(response.data.quotes).map(key => key.substr(3)));
            setConversionRate(response.data.quotes);
        });
    }, []);

    useEffect(() => {
        const rate = conversionRate[`${baseCurrency}${targetCurrency}`];
        setResult(baseAmount * (rate || 1));
        setDate(new Date());
    }, [baseCurrency, targetCurrency, baseAmount, conversionRate]);

    const swapCurrencies = () => {
        setBaseCurrency(targetCurrency);
        setTargetCurrency(baseCurrency);
    };

    return (
        <div>
            <InputEx
                value={baseAmount}
                onChange={(e) => setBaseAmount(e.target.value)}
                currencies={currencies}
                selectedCurrency={baseCurrency}
                onCurrencyChange={(e) => setBaseCurrency(e.target.value)}
            />
            <button onClick={swapCurrencies}>Swap</button>
            <InputEx
                currencies={currencies}
                selectedCurrency={targetCurrency}
                onCurrencyChange={(e) => setTargetCurrency(e.target.value)}
            />
            <ScoreEx result={result} date={date} />
        </div>
    );
}

export default ExchangeApp;
