import React from 'react';

function InputEx({ fromCurrency, toCurrency, amount, onAmountChange, onSwitch, onFromCurrencyChange, onToCurrencyChange }) {
    return (
        <div className="d-flex justify-content-center align-items-center mb-4">
            <select className="form-control mx-2" value={fromCurrency} onChange={(e) => onFromCurrencyChange(e.target.value)}>
                {/* ... [השאר האופציות] */}
            </select>
            <input type="number" className="form-control mx-2" value={amount} onChange={(e) => onAmountChange(Number(e.target.value))} />
            <button className="btn btn-primary mx-2" onClick={onSwitch}>↔</button>
            <select className="form-control mx-2" value={toCurrency} onChange={(e) => onToCurrencyChange(e.target.value)}>
                {/* ... [השאר האופציות] */}
            </select>
        </div>
    );
}

export default InputEx;
